mapper.update:
UPDATE board_main_${pageName}
SET 
	bm_hit = IFNULL(bm_hit,0) + 1
WHERE 
	bm_num = #{bmNum}

mapper.select:
SELECT 
	bm_savedfile 
FROM 
	board_main_${b_id}
WHERE 
	bm_num = #{bm_num}

mapper.update:
UPDATE board_main_${b_id}
SET
	bm_title = #{bm_title},
	bm_writer = #{bm_writer},
	bm_content = #{bm_content},
	bm_file = #{bm_file},
	bm_savedfile = #{bm_savedfile},
	bm_filepath = #{bm_filepath}
WHERE bm_num = #{bm_num}

UPDATE board_reply_${b_id}
SET 
	r_content = #{r_content}
WHERE 
	r_num = #{r_num}
